library(foreign)temp<-read.spss("2008data.sav")write.csv(temp,"data2008.csv",row.names=F)data2008<-read.csv("data2008.csv")mu<-colMeans(data2008[,2:9],na.rm=T);munew_data2008<-na.omit(data2008[,2:9])v=numeric(ncol(new_data2008))for (i in 1:ncol(new_data2008)) {  v[i]=var(new_data2008[i])}var=v;varsd<-var^2;sdcov<-cov(new_data2008);covcor<-cor(new_data2008);corcov_cor<-cov2cor(cov);cov_cor